class Counter:
    def __init__(self):
        self.count = 0
    def increment(self):
        self.count += 1
    def decrement(self):
        self.count -= 1
    def value(self):
        return self.count

import requests

def secure_upload(url, filepath, api_key):
    headers = {
        'Authorization': f'Bearer {api_key}'
    }
    with open(filepath, 'rb') as f:
        r = requests.post(url, files={'file': f}, headers=headers, verify=True)
    return r.status_code
